INSERT INTO `Evidence` VALUES(147,'evidence_1669','http://dbmi-icode-01.dbmi.pitt.edu/linkedSPLs/resource/structuredProductLabelMetadata/33f60b40-3fca-11de-8f56-0002a5d5c51b','Fluoxetine: Co-administration of fluoxetine (20 mg twice daily for 21 days), a potent inhibitor of CYP2D6, with a single 3 mg dose of iloperidone to 23 healthy volunteers, ages 29-44, who were classified as CYP2D6 extensive metabolizers, increased the AUC of iloperidone and its metabolite P88, by about 2-3 fold, and decreased the AUC of its metabolite P95 by one-half. Iloperidone doses should be reduced by one-half when administered with fluoxetine. When fluoxetine is withdrawn from the combination therapy, the iloperidone dose should be returned to the previous level. Other strong inhibitors of CYP2D6 would be expected to have similar effects and would need appropriate dose reductions. When the CYP2D6 inhibitor is withdrawn from the combination therapy, iloperidone dose could then be increased to the previous level.','boycer','01132010','Non_traceable_Drug_Label_Statement','Evidence',NULL,NULL,NULL,NULL,'',NULL,NULL);
INSERT INTO `Evidence` VALUES(148,'evidence_1774','http://dbmi-icode-01.dbmi.pitt.edu/linkedSPLs/resource/structuredProductLabelMetadata/33f60b40-3fca-11de-8f56-0002a5d5c51b','Paroxetine: Co-administration of paroxetine (20 mg/day for 5-8 days), a potent inhibitor of CYP2D6, with multiple doses of iloperidone (8 or 12 mg twice daily) to patients with schizophrenia ages 18-65 resulted in increased mean steady-state peak concentrations of iloperidone and its metabolite P88, by about 1.6 fold, and decreased mean steady-state peak concentrations of its metabolite P95 by one-half. Iloperidone doses should be reduced by one-half when administered with paroxetine. When paroxetine is withdrawn from the combination therapy, the iloperidone dose should be returned to the previous level. Other strong inhibitors of CYP2D6 would be expected to have similar effects and would need appropriate dose reductions. When the CYP2D6 inhibitor is withdrawn from the combination therapy, iloperidone dose could then be increased to previous levels.','boycer','01132010','Non_traceable_Drug_Label_Statement','Evidence',NULL,NULL,NULL,NULL,'',NULL,NULL);
